import React,{Component} from 'react'
import { Provider, Subscribe } from 'unstated'

import styled from 'styled-components'

import TodosContainer from './store'

import TodoList from './components/TodoList'
import AddTodo from './components/AddTodo'

class App extends Component {
  componentWillMount(){
    localStorage.setItem('items', JSON.stringify('list'));

  }
render(){
  console.log( localStorage.getItem('items'),'hello')
  return (
    <Provider>
      <Wrapper>
        <Subscribe to={[TodosContainer]}>
          {todos => {
            const list = todos.getList()
            localStorage.setItem('items', JSON.stringify(list));
            return (
              <TodosWrapper>
                <AddTodo onAddTodo={todos.createTodo} onShowInput={todos.showInput} stateShowInput={todos.state.showInput} onChangeText={todos.onChangeText}text={todos.state.text}/>
                <TodoList items={list} toggleComplete={todos.toggleComplete} />
              </TodosWrapper>
            )
          }}
        </Subscribe>
      </Wrapper>
    </Provider>
  )
 }
}

const Wrapper = styled.div`
  background-color: white;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  font-size: 24px;
  color: white;
  margin-top:40px
  margin-left:50px
`

const TodosWrapper = styled.div`
  display: flex;
  flex-direction: column;
`

export default App
