/* eslint-disable */
import React from 'react'
import Button from 'react-bootstrap/Button';
import ButtonToolbar from 'react-bootstrap/ButtonToolbar';
import styled from 'styled-components'

const AddTodo = ({ onAddTodo, onShowInput, stateShowInput }) => {
  const handleKeyPress = e => {
    if (e.key === 'Enter') {
      onAddTodo(e.target.value)
    }
  }

  const showInput = ()=>{
    onShowInput()
  }
  console.log(stateShowInput,' stateShowInput')

  return (
    <AddWrapper>
    <ButtonToolbar style={{bottom:0}}>
      <Button variant="primary" size="lg" onClick={()=>showInput()}style={{width:300}}>All</Button>
      <Button variant="success" size="lg"  style={{marginLeft:61, width:300}}>Complete</Button>
      <Button variant="danger" size="lg"  style={{marginLeft:61, width:300}}>In-Progress</Button>
    </ButtonToolbar>
    {stateShowInput&&
    <Input
      type='text'
      onKeyPress={handleKeyPress}
      placeholder='Add new todo...'
    />
    }
    </AddWrapper>
  )
}

const Input = styled.input`
  background: #3b4049;
  color: #fff;
  border: none;
  border-radius: 3px;
  padding: 10px 18px;
  font-size: 24px;
  height: 40px;
  width: 300px;
  margin-top:55px
  &::placeholder {
    color: #8d96a8;
  }
`
const AddWrapper = styled.div`
  display: flex;
  flex-direction: column;
`
export default AddTodo
