/* eslint-disable */
import React from 'react'
import Button from 'react-bootstrap/Button';
import ButtonToolbar from 'react-bootstrap/ButtonToolbar';
import styled from 'styled-components'

const AddTodo = ({ onAddTodo, onShowInput, stateShowInput }) => {
  const handleKeyPress = e => {
    console.log(e.target.value,'fgf')
    if (e.key === 'Enter') {
      onAddTodo(e.target.value)
    }
  }

  const showInput = ()=>{
    onShowInput()
  }
  console.log(stateShowInput,' stateShowInput')

  return (
    <AddWrapper>
    <ButtonToolbar >
      <Button variant="primary" size="lg" onClick={()=>showInput()}style={{width:300}}>All</Button>
      <Button variant="success" size="lg"  style={{marginLeft:61, width:300}}>Complete</Button>
      <Button variant="danger" size="lg"  style={{marginLeft:61, width:300}}>In-Progress</Button>
    </ButtonToolbar>

    <InputWrapper>
    <Input
      type='text'
      onKeyPress={handleKeyPress}
      placeholder='Add new todo...'
    />
   <Button variant="secondary" size="sm" style={{width:90, height:45, marginLeft:20}} onClick={(e)=>handleKeyPress(e)}>Add</Button>
   </InputWrapper>
    </AddWrapper>
  )
}

const Input = styled.input`
  background:purple;
  color: #fff;
  border: none;
  border-radius: 3px;
  padding: 10px 18px;
  font-size: 24px;
  height: 45px;
  width: 400px;

  margin-bottom:24px
  &::placeholder {
    color: #8d96a8;
  }
`
const AddWrapper = styled.div`
  display: flex;
  flex-direction: column;
`
const InputWrapper = styled.div`
display: flex;
flex-direction: row;
margin-top:55px
`
export default AddTodo
